import React,{useState} from 'react';
import yaml from 'js-yaml';
import Converter from './Converter.css'
import { TileLayout } from "@progress/kendo-react-layout";
import ReactJson from 'react-json-view';



class ConverterModule extends React.Component {



        constructor(props) {
            super(props);
            this.state = {value: ''};
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
            const sampleData = {
                name: 'Jason Voorhees',
                age: 74,
                hasKilled: true,
                birthday: 'June 13, 1946',
              }
            this.state = {
                justClicked: sampleData,
              };
          }

          handleChange(event) {
            console.log('********* handleChange!  '+ event.target.value)
            this.setState({value: event.target.value});
            const jsonString = JSON.stringify(yaml.load(event.target.value));
            console.log("$$$$$$$$$$$ Resulting Json :"+jsonString);
            this.setState({
                justClicked: JSON.parse(jsonString)
              });
          }

          handleSubmit(event) {

            console.log('********* handleSubmit!  '+ event)
            //alert('A name was submitted: ' + this.state.value);
            event.preventDefault();
          }



      render() {
        return (
<>
<div className="float-container">


    {/* <form  className="green" onSubmit={this.handleSubmit}> */}
          <textarea  className="float-child" type="text" placeholder={this.state.justClicked} value={this.state.value} onChange={this.handleChange} />
          {/* </form> */}

  <div className="float-child">
    <div className="green">
    <ReactJson src={this.state.justClicked}/>
  </div>
  </div>
</div>
</>
);
}
}
export default ConverterModule;
