import React from 'react';
import header from './header.css';

class Header extends React.Component {

    handleClick = (e) => {
            console.log('click!' + e)
            var underlines = document.querySelectorAll(".underline");
            for (var i = 0; i < underlines.length; i++) {
            underlines[i].style.transform = 'translate3d(' + e * 100 + '%,0,0)';
            console.log('Changing color :' + underlines[i].style.transform)
            }
        }
        render() {

    return (
<>
<nav>
      <a onClick={() => this.handleClick(0)}>Home</a>
      <a className="underline" onClick={() => this.handleClick(1)}>Courses</a>
      <a className="underline" onClick={() => this.handleClick(2)}>Code With Me</a>
      <a className="underline" onClick={() => this.handleClick(3)}>All Access</a>
      <a className="underline" onClick={() => this.handleClick(4)}>About</a>
    </nav>
</>

    );
        }
}

export default Header;
