        constructor(props) {
            super(props);
            this.state = {value: ''};
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
          }

          handleChange(event) {

            const [initialValue,setInitialValue] = useState('')
            const [displayText, setDisplayText] = useState(false)
            const [Value,setValue] = useState('')
            console.log('********* handleChange!  '+ event.target.value)
            this.setState({value: event.target.value});
            const jsonString = JSON.stringify(yaml.load(event.target.value));
            console.log("$$$$$$$$$$$ Resulting Json :"+jsonString);
            setInitialValue(jsonString);

          }

          handleSubmit(event) {

            console.log('********* handleSubmit!  '+ event)
            //alert('A name was submitted: ' + this.state.value);
            event.preventDefault();
          }



      render() {
        return (
<>
<div className="float-container">


    <div className="float-child">
    {/* <form  className="green" onSubmit={this.handleSubmit}> */}
          <textarea  className="float-child" type="text" placeholder="Type-in any Yaml text here ....." value={this.state.value} onChange={this.handleChange} />
          {/* </form> */}
    </div>

  <div className="float-child">
    <div className="blue">{displayText && <div>Value: {Value}</div>}</div>
  </div>

</div>
</>
);
}
}
export default ConverterModule;
