import React from 'react';
import Converter from './Converter.css'
import { TileLayout } from "@progress/kendo-react-layout";


class ConverterModule extends React.Component {

        constructor(props) {
            super(props);
            this.state = {value: ''};
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
          }

          handleChange(event) {

            console.log('********* handleChange!')

            this.setState({value: event.target.value});
          }

          handleSubmit(event) {

            console.log('********* handleSubmit!')
            alert('A name was submitted: ' + this.state.value);
            event.preventDefault();
          }
      render() {
        return (
<>
<div className="float-container">
  <div className="float-child">
    <div className="green">

    <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" onChange={this.handleChange} placeholder="Search for any book"/>
      </form>
    </div>
  </div>

  <div className="float-child">
    <div className="blue">Float Column 2</div>
  </div>

</div>
</>
);
}
}
export default ConverterModule;
