import React,{useState} from 'react';
import yaml from 'js-yaml';
import Converter from './Converter.css'
import { TileLayout } from "@progress/kendo-react-layout";
import ReactJson from 'react-json-view';



class ConverterModule extends React.Component {



        constructor(props) {
            super(props);
            this.state = {value: ''};
            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
          }

          handleChange(event) {
            console.log('********* handleChange!  '+ event.target.value)
            this.setState({value: event.target.value});
            const jsonString = JSON.stringify(yaml.load(event.target.value));
            console.log("$$$$$$$$$$$ Resulting Json :"+jsonString);
            this.setState({
                justClicked: jsonString
              });
          }

          handleSubmit(event) {

            console.log('********* handleSubmit!  '+ event)
            //alert('A name was submitted: ' + this.state.value);
            event.preventDefault();
          }



      render() {
        return (
<>
<div className="float-container">


    <div className="float-child">
    {/* <form  className="green" onSubmit={this.handleSubmit}> */}
          <textarea  className="float-child" type="text" placeholder="Type-in any Yaml text here ....." value={this.state.value} onChange={this.handleChange} />
          {/* </form> */}
    </div>

  <div className="float-child">
    <div className="green" theme="monokai">
    <ReactJson src={JSON.stringify(this.state.justClicked, null, 2)} theme="monokai" />
  </div>
  </div>
</div>
</>
);
}
}
export default ConverterModule;
